import React,{useState} from "react"
import { useNavigate } from "react-router-dom";


function Home() {

const navigate = useNavigate()
const [user,setUser]=useState({
    UserName:'',password:''
})
let name,value;
const handleInput=(e)=>{
    console.log(e)
    name=e.target.name;
    value=e.target.value;
    setUser({...user,[name]:value})
}

const postdata=async(e)=>{
    e.preventDefault()
    const {UserName,password}=user

    const res=await fetch("/signup",{
method:"post",
headers:{

    "content-type":"application/json"
},
body:JSON.stringify({
    UserName,password
})

    });

const data=await res.json()
console.log(data)
if(data.status === 422 || !data){
window.alert("invalid register" )
console.log("invalid register") 
navigate("/contect")

}
else{window.alert("valid register")
console.log("valid register") ;
// navigate("/contect")
}}
return(
<>
<div className="container bg-black" >
    <div className=" col-6 border bg-black">

<h1>signup</h1>

        <form  method="post" className="text-light">
<div class=" px-1 my-5">
    <label for="">Email</label> 
    <input type="text" name="UserName" class="py-1 my-2 w-100  " value={user.UserName} onChange={handleInput} autoCorrect="off"/>
</div>
<div class=" px-1 ">
    <label for="">Password</label> 
    <input type="text" name="password" class="py-1 my-2 w-100" value={user.password} onChange={handleInput} autoCorrect="off"/>
</div>


<div class=" px-1 my-5 text-center">
   
    <input type="submit" name="submit" id="" class=" btn btn-outline-light py-1 my-2 w-50 " onClick={postdata}/>
</div>
</form>

</div></div>
</>


)}








export default Home;
